package operatorsExercises;

public class OperatorsExercises {
	public static void main(String[] args) {
		System.out.println(addition(3,4));
		System.out.println(subtraction(3,4));
		System.out.println(multiplication(3,4));
		System.out.println(division(3,4));
	}

	private static double division(double i, double j) {
		// TODO Auto-generated method stub
		return i / j;
	}

	private static int multiplication(int i, int j) {
		// TODO Auto-generated method stub
		return i*j;
	}

	private static int subtraction(int i, int j) {
		// TODO Auto-generated method stub
		return i - j;
	}

	private static int addition(int i, int j) {
		// TODO Auto-generated method stub
		return i + j;
	}

}
